import React from "react";
import { WikiDetail } from "../types";
import { saveArticle } from "../services/api";

interface Props {
  detail: WikiDetail;
  onBack: () => void;
}

const ArticleDetail: React.FC<Props> = ({ detail, onBack }) => {
  const handleSave = async () => {
    await saveArticle(detail.title, detail.url, detail.summary);
    alert("Artículo guardado correctamente.");
  };

  return (
    <div style={{ marginTop: "1rem" }}>
      <button onClick={onBack}>← Volver a resultados</button>
      <h2>{detail.title}</h2>
      <p>
        <a href={detail.url} target="_blank" rel="noopener noreferrer">
          Ver en Wikipedia
        </a>
      </p>
      <button onClick={handleSave}>Guardar Artículo</button>
      <h3>Resumen</h3>
      <p>{detail.summary}</p>
      <h3>Análisis</h3>
      <p>Recuento de palabras: {detail.analysis.word_count}</p>
      <ul>
        {detail.analysis.top_words.map(([word, freq]) => (
          <li key={word}>
            {word}: {freq}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ArticleDetail;
