import React, { useEffect, useState } from "react";
import { SavedArticle } from "../types";
import { getSavedArticles, deleteSavedArticle } from "../services/api";

const SavedArticles: React.FC = () => {
  const [articles, setArticles] = useState<SavedArticle[]>([]);
  const [loading, setLoading] = useState<boolean>(false);

  const loadArticles = async () => {
    setLoading(true);
    try {
      const data = await getSavedArticles();
      setArticles(data);
    } catch (error) {
      console.error("Error cargando artículos guardados:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadArticles();
  }, []);

  const handleDelete = async (id: number) => {
    await deleteSavedArticle(id);
    loadArticles();
  };

  return (
    <div style={{ marginTop: "2rem" }}>
      <h2>Mis Artículos Guardados</h2>
      {loading ? (
        <p>Cargando...</p>
      ) : articles.length === 0 ? (
        <p>No hay artículos guardados.</p>
      ) : (
        <ul>
          {articles.map((article) => (
            <li key={article.id} style={{ marginBottom: "1rem" }}>
              <a href={article.url} target="_blank" rel="noopener noreferrer">
                {article.title}
              </a>
              <button
                onClick={() => handleDelete(article.id)}
                style={{ marginLeft: "1rem" }}
              >
                Eliminar
              </button>
              <p style={{ fontSize: "0.9rem", marginTop: "0.3rem" }}>
                {article.processed_summary}
              </p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SavedArticles;
