import React from "react";
import { WikiDetailResponse } from "../types";
import styles from "../styles/ArticleDetail.module.css";

interface Props {
  article?: WikiDetailResponse;
  onSave: (title: string, url: string, processed_summary: string) => void;
  loading: boolean;
}

const ArticleDetail: React.FC<Props> = ({ article, onSave, loading }) => {
  if (loading) {
    return <p className={styles.loading}>Obteniendo detalles...</p>;
  }
  if (!article) {
    return (
      <p className={styles.placeholder}>
        Selecciona un artículo para ver los detalles.
      </p>
    );
  }

  const { title, summary, analysis, url } = article;

  return (
    <div className={styles.container}>
      <h2>{title}</h2>
      <p className={styles.summary}>{summary}...</p>
      <p>
        <strong>Palabras totales:</strong> {analysis.word_count}
      </p>
      <p>
        <strong>Top palabras:</strong> {analysis.top_words.join(", ")}
      </p>
      <a href={url} target="_blank" rel="noopener noreferrer">
        Ver en Wikipedia
      </a>
      <button
        className={styles.saveButton}
        onClick={() => onSave(title, url, summary)}
      >
        Guardar Artículo
      </button>
    </div>
  );
};

export default ArticleDetail;
