import React, { useState } from "react";
import SearchBar from "./components/SearchBar";
import SearchResults from "./components/SearchResults";
import ArticleDetail from "./components/ArticleDetail";
import SavedArticles from "./components/SavedArticles";

import {
  searchWikipedia,
  getWikiDetail,
  saveArticle,
} from "./services/api";
import { WikiSearchItem, WikiDetailResponse } from "./types";

import styles from "./styles/App.module.css";

const App: React.FC = () => {
  const [searchResults, setSearchResults] = useState<WikiSearchItem[]>([]);
  const [selected, setSelected] = useState<WikiSearchItem | null>(null);
  const [detail, setDetail] = useState<WikiDetailResponse | null>(null);
  const [loadingSearch, setLoadingSearch] = useState<boolean>(false);
  const [loadingDetail, setLoadingDetail] = useState<boolean>(false);
  const [errorSearch, setErrorSearch] = useState<string>("");
  const [errorDetail, setErrorDetail] = useState<string>("");

  const handleSearch = async (term: string) => {
    setErrorSearch("");
    setLoadingSearch(true);
    setSearchResults([]);
    setSelected(null);
    setDetail(null);
    try {
      const data = await searchWikipedia(term);
      setSearchResults(data.results);
    } catch (err) {
      setErrorSearch("Error al buscar en Wikipedia.");
    } finally {
      setLoadingSearch(false);
    }
  };

  const handleSelect = async (item: WikiSearchItem) => {
    setSelected(item);
    setDetail(null);
    setErrorDetail("");
    setLoadingDetail(true);
    try {
      const data = await getWikiDetail(item.pageid, item.title);
      setDetail(data);
    } catch (err) {
      setErrorDetail("Error al obtener detalles del artículo.");
    } finally {
      setLoadingDetail(false);
    }
  };

  const handleSave = async (
    title: string,
    url: string,
    processed_summary: string
  ) => {
    try {
      await saveArticle(title, url, processed_summary);
      alert("Artículo guardado correctamente.");
    } catch (err: any) {
      if (err.response && err.response.status === 409) {
        alert("El artículo ya está guardado.");
      } else {
        alert("Error al guardar el artículo.");
      }
    }
  };

  return (
    <div className={styles.appContainer}>
      <h1>Wikipedia Curator</h1>
      <SearchBar onSearch={handleSearch} />
      {errorSearch && <p className={styles.error}>{errorSearch}</p>}
      <SearchResults
        results={searchResults}
        onSelect={handleSelect}
        loading={loadingSearch}
      />
      {errorDetail && <p className={styles.error}>{errorDetail}</p>}
      <ArticleDetail
        article={detail || undefined}
        onSave={handleSave}
        loading={loadingDetail}
      />
      <SavedArticles />
    </div>
  );
};

export default App;
