import axios from "axios";
import { WikiSearchResult, WikiDetail } from "../types";

const API_BASE_URL = "http://localhost:8000";

export const searchWikipedia = async (term: string) => {
  const res = await axios.get(`${API_BASE_URL}/search?q=${term}`);
  return res.data;
};

export const getWikiDetail = async (pageid: number, title: string) => {
  const res = await axios.get(`${API_BASE_URL}/summary?pageid=${pageid}&title=${title}`);
  return res.data;
};

export const getSavedArticles = async () => {
  const res = await axios.get(`${API_BASE_URL}/articles`);
  return res.data;
};

export const saveArticle = async (title: string, url: string, processed_summary: string) => {
  const res = await axios.post(`${API_BASE_URL}/articles`, {
    title,
    url,
    processed_summary,
  });
  return res.data;
};

export const deleteSavedArticle = async (id: number) => {
  await axios.delete(`${API_BASE_URL}/articles/${id}`);
};
