// frontend/src/App.tsx

import React, { useState } from "react";
import SearchBar from "./components/SearchBar";
import SearchResults from "./components/SearchResults";
import ArticleDetail from "./components/ArticleDetail";
import { WikiSearchResult, WikiDetail } from "./types";
import { searchWikipedia, getWikiDetail } from "./services/api";

const App: React.FC = () => {
  // Estado para la lista de resultados de búsqueda
  const [results, setResults] = useState<WikiSearchResult[]>([]);
  // Estado para el artículo seleccionado (detalle)
  const [selectedDetail, setSelectedDetail] = useState<WikiDetail | null>(null);
  // Estado para mostrar un loading al buscar o al pedir detalle
  const [loading, setLoading] = useState<boolean>(false);
  // Estado para manejar errores
  const [error, setError] = useState<string | null>(null);

  // Función que se le pasa a SearchBar
  const handleSearch = async (term: string) => {
    setError(null);
    setSelectedDetail(null);
    setLoading(true);
    try {
      // searchWikipedia retorna un objeto WikiSearchResponse, extraemos el array de resultados
      const response = await searchWikipedia(term);
      setResults(response.results); // Asegúrate de que 'results' es el nombre correcto del array en WikiSearchResponse
    } catch (e: any) {
      console.error(e);
      setError("Error al buscar en Wikipedia. Intenta nuevamente.");
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  // Función que se le pasa a SearchResults
  const handleSelect = async (pageid: number, title: string) => {
    setError(null);
    setLoading(true);
    try {
      // getWikiDetail recibe el pageid y el título y devuelve un WikiDetailResponse
      const detailResponse = await getWikiDetail(pageid, title);
      const detail: WikiDetail = { 
        ...detailResponse, 
        pageid,
        analysis: {
          ...detailResponse.analysis,
          top_words: Array.isArray(detailResponse.analysis.top_words)
            ? detailResponse.analysis.top_words.map((word: string) => [word, 1])
            : []
        }
      };
      setSelectedDetail(detail);
    } catch (e: any) {
      console.error(e);
      setError("Error al obtener detalle del artículo.");
    } finally {
      setLoading(false);
    }
  };

  // Función para "volver" a la lista
  const handleBack = () => {
    setSelectedDetail(null);
  };

  return (
    <div style={{ maxWidth: "800px", margin: "2rem auto", padding: "0 1rem" }}>
      <h1 style={{ textAlign: "center", marginBottom: "2rem" }}>
        Wiki-Analyzer (React + FastAPI)
      </h1>

      <SearchBar onSearch={handleSearch} />

      {loading && <p>Cargando...</p>}

      {error && (
        <p style={{ color: "red", marginTop: "1rem" }}>
          {error}
        </p>
      )}

      {/* Si hay un artículo seleccionado, mostramos ArticleDetail */}
      {selectedDetail ? (
        <ArticleDetail detail={selectedDetail} onBack={handleBack} />
      ) : (
        /* En caso contrario, mostramos la lista de resultados (si los hay) */
        <>
          {results.length > 0 && (
            <>
              <h2 style={{ marginTop: "1rem" }}>
              Resultados de búsqueda:
              </h2>
              <SearchResults
              results={results}
              // Adapt onSelect to accept both pageid and title
              onSelect={handleSelect}
              />
            </>
          )}
        </>
      )}
    </div>
  );
};

export default App;
