import React from "react";
import { WikiSearchItem } from "../types";
import styles from "../styles/SearchResults.module.css";

interface Props {
  results: WikiSearchItem[];
  onSelect: (item: WikiSearchItem) => void;
  loading: boolean;
}

const SearchResults: React.FC<Props> = ({ results, onSelect, loading }) => {
  if (loading) {
    return <p className={styles.loading}>Cargando resultados...</p>;
  }

  if (results.length === 0) {
    return <p className={styles.noResults}>No se encontraron resultados.</p>;
  }

  return (
    <ul className={styles.resultsList}>
      {results.map((item) => (
        <li
          key={item.pageid}
          className={styles.resultItem}
          onClick={() => onSelect(item)}
        >
          {item.title}
        </li>
      ))}
    </ul>
  );
};

export default SearchResults;
